mapper.select:
SELECT ORD.ORD_NO AS ORD_NO , FN_CODE_NM('001',ORD.ORD_STAT_CD) AS ORD_STAT_NM , ORD.CUST_CD AS CUST_NO , CUST.CUST_NM AS CUST_NM , CUST.CUST_GBCD AS CUST_GBCD_NM , CUST.PHONE AS PHONE , CUST.ADDR AS ADDR , ITEM.ITEM_NM AS ITEM_NM , ORD.REG_DT AS REG_DT FROM TB_ORD ORD LEFT JOIN TB_CUST CUST ON ORD.CUST_CD = CUST.CUST_CD LEFT JOIN TB_ITEM ITEM ON ORD.ITEM_CD = ITEM.ITEM_CD WHERE 1=1
AND ORD.ORD_NO = #{ORD_NO}
AND CUST.CUST_NM = #{CUST_NM}
AND CUST.CUST_GBCD = 'C'
AND ORD.ORD_STAT_CD = #{ORD_STAT_CD}
AND CUST.CUST_GBCD = #{CUST_GBCD}

mapper.update:
update tbl_board 
   set title       = #{title}
     , regId       = #{regId}
     , contents    = #{contents}
     , communityId = #{communityId}
where postId=#{postId}

